import { Component, OnInit } from '@angular/core';
import { ProductosService } from '../services/productos.service';

@Component({
  selector: 'app-producto',
  templateUrl: './producto.component.html',
  styleUrls: ['./producto.component.css']
})
export class ProductoComponent implements OnInit{
  productos: any[]=[]

  constructor(private productosService: ProductosService){}

  ngOnInit(): void {
    this.productosService.getProductos().subscribe(
      (productos) => {
        // Hacer una copia del array de productos para no mutar el original
        this.productos = [...productos];
      },
      (error) => {
        console.error('Error al obtener productos', error);
      }
    );
  }

  ordenar(tipo: string) {
    // Antes de ordenar, verifica que productos es un array
    if (this.productos && this.productos.length > 0) {
      let productosOrdenados = [...this.productos]; // Hacer una copia del array para ordenar
      if (tipo === 'precioAsc') {
        productosOrdenados.sort((a, b) => a.precio - b.precio);
      } else if (tipo === 'precioDesc') {
        productosOrdenados.sort((a, b) => b.precio - a.precio);
      } else if (tipo === 'alfabetico') {
        productosOrdenados.sort((a, b) => a.nombre.localeCompare(b.nombre));
      }
      this.productos = productosOrdenados; // Actualizar el array de productos con la lista ordenada
    }
  }
}
<main class="container">
  <h2>Catálogo de Productos</h2>
  <div>
    <button (click)="ordenar('precioAsc')">Ordenar por precio: bajo a alto</button>
    <button (click)="ordenar('precioDesc')">Ordenar por precio: alto a bajo</button>
    <button (click)="ordenar('alfabetico')">Ordenar alfabéticamente</button>
  </div>
  <div id="products-grid" class="product-grid">
    <!-- Productos en un contenedor flex con dirección de columnas -->
    <div class="product" *ngFor="let pro of productos | async">
      <img [src]="pro.url" alt="Imagen del producto" class="product-image">
      <div class="product-info">
        <h3 class="product-name">{{ pro.nombre }}</h3>
        <p class="product-collection">{{ pro.categoria }}</p>
        <p class="product-price">{{ pro.precio | currency }}</p>
      </div>
    </div>
  </div>
</main>

<footer class="container">
  <small>
    <a href="#">Sobre la tienda</a> • 
    <a href="#">Términos y Condiciones</a>
  </small>
</footer>
