import { Component, OnInit } from '@angular/core';
import { ProductosService } from '../services/productos.service';
import { Producto } from 'src/app/domain/producto';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-carrito',
  templateUrl: './carrito.component.html',
  styleUrls: ['./carrito.component.css']
})
export class CarritoComponent implements OnInit {
  productos: Producto[] = []; // Un array para almacenar los productos

  constructor(private productosService: ProductosService) { } // Inyecta tu servicio aquí

  ngOnInit(): void {
    this.cargarProductos();
  }

  cargarProductos(): void {
    this.productosService.getCarrito().subscribe(
      (response) => {
        if (Array.isArray(response)) {
          this.productos = response;
        } else {
          // Manejar mensaje de carrito vacío
        }
      },
      (error) => {
        console.error('Error al obtener los productos', error);
      }
    );
  }
}
