import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/app/enviroment/environment';
import { Producto } from 'src/app/domain/producto';

@Injectable({
  providedIn: 'root'
})
export class ProductosService {

  constructor(private http: HttpClient) { }

  getProductos(){
    let url = `${environment.WS_PATH}/productos/list/`;
    return this.http.get<Producto[]>(url);
  }

  getCarrito(){
    let url = `${environment.WS_PATH}/productos/mostrar/`;
    return this.http.get<Producto[]>(url);
  }

  addToCart(productoId: Producto, usuario: string){
    let url = `${environment.WS_PATH}/productos/carrito/`;
    let body = { producto: productoId, usuario: usuario };
    return this.http.post<any>(url, body);
  }
}
