import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/app/enviroment/environment';
import { Carrito } from 'src/app/domain/carrito';
import { Observable } from 'rxjs';
import { Producto } from 'src/app/domain/producto';
import { Cliente } from 'src/app/domain/cliente';

@Injectable({
  providedIn: 'root'
})
export class CarritoService {

  constructor(private http: HttpClient) { }

  getCarritosporUsuario(cliente:Persona){
    let url = environment.WS_PATH + "/carrito/cliente"
    return this.http.post<any>(url,persona)
  }

  getProductosCarrito(id:any){
      let url = `${environment.WS_PATH}/carrito/lista/productos/${id}`
      return this.http.get<any>(url)
  }

  addProductoAlCarrito(producto:any, codigo:number){
      let url = `${environment.WS_PATH}/carrito/lista/agregar-producto/${codigo}`
      return this.http.post<any>(url,producto)
}
}
