import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ClienteService } from '../services/cliente.service';
import { Router } from '@angular/router'; // Importa Router
import { Cliente } from 'src/app/domain/cliente';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
  signUpForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private clienteService: ClienteService, private router: Router, private aut) {
    this.signUpForm = this.formBuilder.group({
      nombre: ['', Validators.required],
      dni: ['', Validators.required],
      direccion: ['', Validators.required],
      saldo: [0, [Validators.required, Validators.min(0)]],
      usuario: ['', Validators.required],
      contras: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.signUpForm.valid) {
      const cliente: Cliente = this.signUpForm.value;

      this.authService.setCurrentUser(cliente); // Asumiendo que solo quieres almacenar el nombre de usuario
      this.clienteService.saveClientes(cliente).subscribe(
        response => {
          console.log('Registro exitoso', response);
          // Aquí puedes manejar la respuesta del servidor, como redirigir al usuario o mostrar un mensaje de éxito
          this.router.navigate(['/paginas/login']);
        },
        error => {
          console.error('Error al registrar el cliente', error);
          // Aquí puedes manejar errores, como mostrar un mensaje al usuario
        }
      );
    }
  }
}
