import { Component } from '@angular/core';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {
  paginas = [
    {titulo: 'Inicio', path: 'paginas/inicio'},
    {titulo: 'Producto', path: 'paginas/producto'},
  ]
}
