import { Component, OnInit } from '@angular/core';
import { ProductosService } from '../services/productos.service';
import { Producto } from 'src/app/domain/producto';
import { Observable } from 'rxjs';
import { CarritoService } from '../services/carrito.service';

@Component({
  selector: 'app-carrito',
  templateUrl: './carrito.component.html',
  styleUrls: ['./carrito.component.css']
})
export class CarritoComponent implements OnInit {
  carritos: any// Un array para almacenar los productos

  constructor(private carritosService: CarritoService) { } // Inyecta tu servicio aquí

  ngOnInit(): void {
    this.carritosService.getCarritos().subscribe(
      (response: any) => { // Cambiar a 'any' si la respuesta puede no ser un Producto[]
        if (Array.isArray(response)) {
          this.carritos = response;
        } else {
          console.error('Se esperaba un array, se recibió:', response);
          // Aquí podrías manejar la situación, por ejemplo mostrando un mensaje de que el carrito está vacío.
        }
      },
      (error) => {
        console.error('Error al obtener los productos', error);
      }
    );
  }
}
