import { Injectable } from '@angular/core';
import { Cliente } from 'src/app/domain/cliente';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor() {}

  // Método para establecer el usuario actual
  setCurrentUser(dni: Cliente): void {
    localStorage.setItem('currentUser', JSON.stringify(cliente));
    console.log(cliente)
  }

  // Método para obtener el usuario actual
  getCurrentUser(): Cliente | null {
    const clienteJSON = localStorage.getItem('currentUser');
    if (clienteJSON) {
      return JSON.parse(clienteJSON) as Cliente;
    }
    return null;
  }

  // Método para eliminar el usuario actual de la sesión
  clearCurrentUser(): void {
    localStorage.removeItem('currentUser');
  }
}
