import { Component, OnInit } from '@angular/core';
import { ProductosService } from '../services/productos.service';
import { Producto } from 'src/app/domain/producto';
import { AuthService } from '../services/auth.service';
import { CarritoService } from '../services/carrito.service';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-producto',
  templateUrl: './producto.component.html',
  styleUrls: ['./producto.component.css']
})
export class ProductoComponent implements OnInit{
  textoBusqueda: string = '';
  productos: any[]=[];
  productosSeleccionados: Producto[] = [];
  productosFiltrados: Producto[] = [];
  productosSeleccionado: any;
  paginaActual: number = 1;
  productosPorPagina: number = 20; // 5 columnas x 4 filas

  constructor(private productosService: ProductosService, private authService: AuthService, private carritosService: CarritoService){}

  ngOnInit(): void {
    this.productosService.getProductos().subscribe(
      (productos) => {
        // Hacer una copia del array de productos para no mutar el original
        this.productos = [...productos];
      },
      (error) => {
        console.error('Error al obtener productos', error);
      }
    );
  }

  personaAutenticada = this.authService.getUsuarioAutenticado().correo
  codigoAutenticada = this.authService.getUsuarioAutenticado().codigo

  buscar() {
    if (this.textoBusqueda.trim() === '') {
      // Si el campo de búsqueda está vacío, mostrar todos los productos
      this.productosFiltrados = this.productos;
    } else {
      // Filtrar los productos según el texto de búsqueda
      this.productosFiltrados = this.productos.filter(producto =>
        producto.nombre.toLowerCase().includes(this.textoBusqueda.toLowerCase())
      );
    }
  }

  ordenar(tipo: string) {
    let productosOrdenados = [...this.productos];
    if (tipo === 'precioAsc') {
      productosOrdenados.sort((a, b) => (a.precio ?? 0) - (b.precio ?? 0));
    } else if (tipo === 'precioDesc') {
      productosOrdenados.sort((a, b) => (b.precio ?? 0) - (a.precio ?? 0));
    } else if (tipo === 'alfabetico') {
      productosOrdenados.sort((a, b) => (a.nombre ?? "").localeCompare(b.nombre ?? ""));
    }
    this.productos = productosOrdenados;
  }

  totalPaginas(): number {
    return Math.ceil(this.productos.length / this.productosPorPagina);
  }

  siguientePagina(): void {
    if (this.paginaActual < this.totalPaginas()) {
      this.paginaActual++;
    }
  }

  anteriorPagina(): void {
    if (this.paginaActual > 1) {
      this.paginaActual--;
    }
  }

  irAPagina(num: number): void {
    this.paginaActual = num;
  }


  // Función para crear un array de números de páginas para el *ngFor del paginador
  obtenerNumerosDePagina(): number[] {
    return Array(this.totalPaginas()).fill(0).map((x, i) => i + 1);
  }

  agregarAlCarrito(pro: Producto): void {
    console.log("codigo:"+this.codigoAutenticada);
    if (this.codigoAutenticada !== null) {
      this.carritosService.agregarProductoACarrito(pro, this.codigoAutenticada).subscribe(
        response => {
          if (typeof response === 'string') {
            // La respuesta es un mensaje de éxito como texto
            console.log(response);
            // Puedes mostrar este mensaje en tu interfaz de usuario si es necesario
          } else {
            // La respuesta es un objeto JSON válido, puedes manejarlo según corresponda
            console.log(response);
          }
        },
        error => {
          console.error(error);
          // Manejar el error según tus necesidades
        }
      );
    } else {
      console.error('this.codigoAutenticada es null, no se puede llamar a agregarProductoACarrito');
      // Puedes manejar este caso de error de acuerdo a tus necesidades
    }

  }

}
<main class="container">
  <h2>Catálogo de Productos</h2>
  <label for="searchInput">Buscar:</label>
  <input type="text" id="searchInput" [(ngModel)]="textoBusqueda">
  <button (click)="buscar()">Buscar</button>
  <label for="sortMenu">Ordenar por:</label>
  <select id="sortMenu" (change)="ordenar(sortMenu.value)" #sortMenu>
    <option value="precioAsc">Ordenar por Precio: bajo a alto</option>
    <option value="precioDesc">Ordenar por Precio: alto a bajo</option>
    <option value="alfabetico">Ordenar por Categoria</option>
  </select>
  <div class="pagination-controls">
    <button (click)="anteriorPagina()" [disabled]="paginaActual === 1">&lt;</button>
    <button *ngFor="let num of obtenerNumerosDePagina()" (click)="irAPagina(num)" [class.active]="num === paginaActual">{{ num }}</button>
    <button (click)="siguientePagina()" [disabled]="paginaActual === totalPaginas()">&gt;</button>
  </div>
  <div id="products-grid" class="product-grid">
    <!-- Productos en un contenedor flex -->
    <div class="product" *ngFor="let pro of productosFiltrados | slice:(paginaActual - 1) * productosPorPagina : paginaActual * productosPorPagina">
      <img [src]="pro.url" alt="Imagen del producto">
      <p>{{ pro.nombre }}</p>
      <p>{{ pro.categoria }}</p>
      <p>{{ pro.precio }}</p>
      <button (click)="agregarAlCarrito(pro)">+</button> <!-- Botón añadir al carrito -->
    </div>
  </div>
</main>

<footer class="container">
  <small>
    <a href="#">Sobre la tienda</a> • 
    <a href="#">Términos y Condiciones</a>
  </small>
</footer>
