import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router'; // Importa Router
import { AuthService } from '../services/auth.service';
import { ClienteService } from '../services/cliente.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private router: Router, private authService: AuthService, privvate clienteService: ClienteService) { // Inyecta Router aquí
    this.loginForm = this.formBuilder.group({
      usuario: ['', Validators.required],
      contras: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.loginForm.valid) {
      console.log(this.loginForm.value);
      this.authService.setCurrentUser(this.loginForm.value.usuario)
      // Aquí llamarías a tu servicio de autenticación y, después de un inicio de sesión exitoso:
      this.router.navigate(['/paginas/inicio']); // Redirige al usuario a la página de inicio
    }
  }
}
