import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/app/enviroment/environment';
import { Carrito } from 'src/app/domain/carrito';
import { Observable } from 'rxjs';
import { Producto } from 'src/app/domain/producto';

@Injectable({
  providedIn: 'root'
})
export class CarritoService {

  constructor(private http: HttpClient) { }

  getCarritos(){
    let url = `${environment.WS_PATH}/carritos/list/`;
    return this.http.get<Carrito[]>(url);
  }


  addToCart(producto: Producto, usuarioCliente: string): Observable<any> {
    const url = `${environment.WS_PATH}/carrito/agregar`;
    const body = { producto, usuarioCliente };
    return this.http.post(url, body);
  }
}
