import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/app/enviroment/environment';
import { Carrito } from 'src/app/domain/carrito';
import { Observable } from 'rxjs';
import { Producto } from 'src/app/domain/producto';
import { Cliente } from 'src/app/domain/cliente';

@Injectable({
  providedIn: 'root'
})
export class CarritoService {

  constructor(private http: HttpClient) { }

  getCarritos(){
    let url = `${environment.WS_PATH}/carritos/list/`;
    return this.http.get<Carrito[]>(url);
  }


  addToCart(producto: Producto, cliente: Cliente): Observable<any> {
    const url = `${environment.WS_PATH}/carritos/`;
    const carritoProducto = { producto: producto, cliente: cliente };
    return this.http.post(url, carritoProducto); // Solo dos argumentos: URL y cuerpo
  }
}
