import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router'; // Importa Router
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private router: Router, private authService: AuthService) { // Inyecta Router aquí
    this.loginForm = this.formBuilder.group({
      usuario: ['', Validators.required],
      contras: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.loginForm.valid) {
      console.log(this.loginForm.value);
      this.authService.setCurrentUser()
      // Aquí llamarías a tu servicio de autenticación y, después de un inicio de sesión exitoso:
      this.router.navigate(['/paginas/inicio']); // Redirige al usuario a la página de inicio
    }
  }
}
<div class="login-container">
  <h2>Iniciar Sesión</h2>
  <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="usuario">Usuario:</label>
      <input id="usuario" formControlName="usuario" type="text" placeholder="Usuario" required>
    </div>
    <div class="form-group">
      <label for="contras">Contraseña:</label>
      <input id="contras" formControlName="contras" type="password" placeholder="Contraseña" required>
    </div>
    <div class="form-actions">
      <button type="submit" [disabled]="!loginForm.valid">Iniciar Sesión</button>
    </div>
  </form>
  <p>
    ¿No tienes una cuenta? <a href="/paginas/signup">Regístrate aquí</a>
  </p>
</div>
